#import "DisclaimerView.h"
#import <QuartzCore/QuartzCore.h>

@implementation DisclaimerView

- (id)initWithFrame:(CGRect)frame
{
    self = [super initWithFrame:frame];
    if (self) {
        self.backgroundColor = [UIColor blackColor];
        
        UIButton *backButton = [UIButton buttonWithType:UIButtonTypeCustom];
        [backButton setFrame:CGRectMake(self.frame.size.width - 50, 0, 50, 50)];
//        [backButton setTitle:@"BACK" forState:UIControlStateNormal];
        [backButton setBackgroundImage:[UIImage imageNamed:@"back_button.png"] forState:UIControlStateNormal];
        [backButton addTarget:self action:@selector(back) forControlEvents:UIControlEventTouchUpInside];
        [self addSubview:backButton];
        
        [self.layer setCornerRadius:25.0f];
        [self.layer setMasksToBounds:YES];
    }
    return self;
}

-(void)back {
    
    [self removeFromSuperview];
}

